import React from "react"
import SearchButton from "./SearchButton"
import SingleMovie from "./SingleMovie"


import '../style.css'
class SearchBar extends React.Component{

  const

    constructor(){
      super()
      this.handleTextChange = this.handleTextChange.bind(this)
      this.getMovies = this.getMovies.bind(this)
      this.formSuggestions = this.formSuggestions.bind(this)
    }

    handleTextChange(event){
        const {value,type} = event.target
        if(value.length >=3){
          this.getMovies(value)
        }
    }

    getMovies(keyword){
      fetch('https://api.themoviedb.org/3/search/movie?api_key=ba0ac7475d9d6eef735d172d922bea03&language=en-US&query='+keyword)
      .then((response) => {
        return response.json()
      }).then((response) => {
          response.results.sort((a,b) => a.popularity > b.popularity)
          response.results.slice(0,8)
          this.formSuggestions(JSON.stringify(response.results))
        }
      )
    }



    formSuggestions(data){
      for (var i = 0; i < 8; i++) {
          console.log(data[i])
      }
    }

    render(){

      const test ={
        width: "100%",
        height: "100%",
        display: "flex",
        alignItems: "center",
        marginLeft: "2%",
        marginRight: "2%"
      }

      const movies = {
        width: "96%",
        top: "85px",
        background: "whitesmoke",
        borderRadius: "0px 0px 5px 5px",
        position: "absolute",
        border:"0px",
        boxShadow: "0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)"
      }

      //const moviesList = todosData.map( => <TodoItem key={item.id} item={item}/>)

      return (
        <div style={test}>
          <div className="searchBar">
          <input
              type="text"
              name="query"
              placeholder="Enter movie name"
              onChange={this.handleTextChange}/>
          <SearchButton />
          </div>

          <div style={movies}>
            <SingleMovie />
            <SingleMovie />
            <SingleMovie />
            <SingleMovie />

          </div>

        </div>
      )
    }
}

export default SearchBar
