import React, {useCallback} from "react"
import SearchButton from "./SearchButton"
import SingleMovie from "./SingleMovie"


import '../style.css'
class SearchBar extends React.Component{




    constructor(){
      super()
      this.state = {components: []}
      this.handleTextChange = this.handleTextChange.bind(this)
      this.getMovies = this.getMovies.bind(this)
      this.formSuggestions = this.formSuggestions.bind(this)
      this.debounce = this.debounce.bind(this)
    }

    debounce(func, wait, immediate) {
	     var timeout;
	     return function() {
		       var context = this, args = arguments;
		         var later = function() {
			            timeout = null;
			               if (!immediate) func.apply(context, args);
		                 };
		                   var callNow = immediate && !timeout;
		                     clearTimeout(timeout);
		                       timeout = setTimeout(later, wait);
		                         if (callNow) func.apply(context, args);
	                          };
                          };

    handleTextChange(event){
        const {value,type} = event.target
        if(value.length >=3){
          this.debounce(this.getMovies(value),2000)
        }else{
          this.setState({components: []})
        }
    }

    getMovies(keyword){
      fetch('https://api.themoviedb.org/3/search/movie?api_key=ba0ac7475d9d6eef735d172d922bea03&language=en-US&query='+keyword)
      .then((response) => {
        return response.json()
      }).then((response) => {
          this.formSuggestions(response.results.slice(0,8))
        }
      )
    }



    formSuggestions(data){
        const moviesList = data.map( (item) => <SingleMovie key={item.id} item={item}/>)
        this.setState({components: moviesList})
    }

    render(){

      const test ={
        width: "100%",
        height: "100%",
        display: "flex",
        alignItems: "center",
        marginLeft: "2%",
        marginRight: "2%"
      }

      const movies = {
        width: "96%",
        top: "85px",
        background: "whitesmoke",
        borderRadius: "0px 0px 5px 5px",
        position: "absolute",
        border:"0px",
        boxShadow: "0 4px 8px 0 rgba(0, 0, 0, 0.2), 0 6px 20px 0 rgba(0, 0, 0, 0.19)"
      }

      return (
        <div style={test}>
          <div className="searchBar">
          <input
              type="text"
              name="query"
              placeholder="Enter movie name"
              onChange={this.handleTextChange}/>
          <SearchButton />
          </div>

          <div style={movies}>
            {this.state.components}
          </div>

        </div>
      )
    }
}

export default SearchBar
